import './App.css';
import books from './book-database.json'

function App() {
  return (
    <div className="App">
      {books.map(book => (
        <div class="Book" key={book}>
        <div class="Book-title">{book.title}</div>
        <div class="Book-author">{book.author}</div>
        <div class="Book-description">{book.shortDescription}</div>
        <button type="button" class="showMore" aria-expanded="false" aria-controls="Full-Description">More Information</button>
        <div class="Full-Description">
        <div class="Book-url">URL: {book.url}</div>
        <div class="Book-publisher">Publisher: {book.publisher}</div>
        <div class="Book-pubdate">Publication Date: {book.publicationDate}</div>
        <div class="Book-pubdate">Full Description: {book.detailedDescription}</div>
        </div>
        </div>
      ))}
    </div>
  );
}

export default App;
