import './App.css';
import books from './book-database.json'

function App() {
  return (
    <div className="App">
      {books.map(book => (
        <div class="Book" key={book}>
        <div class="Book-title">{book.title}</div>
        <div class="Book-author">{book.author}</div>
        <div class="Book-description">{book.shortDescription}</div>
        <button type="button" aria-expanded="false" aria-controls="Full-Description">More Information</button>
        </div>
      ))}
    </div>
  );
}

export default App;
